                client = null;
            }
        }
        catch (Exception e)
        {
            e.printStackTrace();
            System.err.println("Exception e = "+e.getMessage());
            if (client != null)
            {
                client.disconnect(); 
                client.close();
                client = null;
            }
        }
        return null;
    }
