private void processTokenAddOrUpdate(ChildData data) throws IOException {
    Stat stat = null;
    try {
      stat = zkClient.checkExists().forPath(data.getPath());
    } catch (Exception e) {
      LOG.warn("Could not get path for Token Add/Update notification.. going to update !!", e);
