private <OUTKEY, OUTVALUE>
void closeQuietly(RecordWriter<OUTKEY, OUTVALUE> c, Reporter r) {
  if (c != null) {
    try {
      c.close(r);
    } catch (Exception e) {
      LOG.info("Exception in closing " + c, e);
    }
  }
}
