    @@ -248,12 +248,6 @@ private synchronized void rotate(long lat, long count, long timeSpent, long targ
             return Utils.zeroIfNaNOrInf(((double) totalLat) / totalCount);
         }

    -    public void close() {
    -        if (_task != null) {
    -            _task.cancel();
