-      app.diagnostics.append(getAppKilledDiagnostics());
+      app.diagnostics.append(event.getDiagnostics());
       super.transition(app, event);
     };
   }

-  private static String getAppKilledDiagnostics() {
-    return "Application killed by user.";
-  }
-
