	@Override
    public boolean exec() throws KeeperException, InterruptedException {
        String path = args[1];
        Stat stat = new Stat();
        List<ACL> acl = zk.getACL(path, stat);
        for (ACL a : acl) {
            out.println(a.getId() + ": "
                        + getPermString(a.getPerms()));
        }

        if (cl.hasOption("s")) {
            new StatPrinter(out).print(stat);
        }
        return false;
    }
